import sounddevice as sd

def seq_hz(freqs, dur=0.5, time_unit=1, sil_dur=0.5, fs=44200):
    res = []
    i = 0
    for freq in freqs:
        frames = int((dur[i]/time_unit) * fs)
        t = np.arange(frames) / fs
        res.append(np.sin(2*np.pi*freq*t))
        res.append(np.zeros(1))
        i = i + 1
    return np.concatenate(res)

